mapper.select:
select count(*) from member where
member_name like '%' || #{keyword} || '%'
member_no = #{keyword}
status = #{keyword}

select member_no ,member_name ,nickname ,phone ,address ,to_char(enroll_date, 'YYYY-MM-DD') as "enroll_date" ,decode(status,'Y','활동','N','탈퇴','B','블랙') "status" from member where
member_name like '%' || #{keyword} || '%'
member_no = #{keyword}
status = #{keyword}
