mapper.insert:
insert
  into
  	   board
  	   (
  	   board_no
  	   ,member_no
  	   ,board_title
  	   ,board_content
  	   ,category
  	   )
values
	   (
	   seq_bod_no.nextval
	   ,#{memberNo}
	   ,#{boardTitle}
	   ,#{boardContent}
	   ,#{category}
	   )

mapper.update:
update
	   board
   set 
   	   board_title = #{boardTitle}
   	   ,board_content = #{boardContent}
 where
 	   board_no = #{boardNo}

update
	   Attachment
   set
   	   origin_name = #{originName}
   	   ,change_name = #{changeName}
 where
 	   ref_no = #{boardNo}
   and
       file_category = 3

